mapper.select:
select
from t_sso_user_role ur LEFT JOIN t_sso_user user on ur.user_id = user.id left join t_sso_role role on ur.role_id = role.id left join t_sso_role_permission rp on rp.role_id = role.id left join t_sso_permission permission on permission.id = rp.permission_id where 1 = 1
AND user.id = #{userId};
